const apiPrefix = 'https://accounts.spotify.com/api';
const base64credentials = 'MTg4YjZkZjliMWM3NGVjMTg4NWVkZTZkYzgyZWEzZDQ6MTE1M2FjMDZmNGY1NDgwY2IzY2M2M2ZjYjIzYTBkYTc=';
export default async () => {
  console.log('token begin');
  const res = await fetch(`${apiPrefix}/token`, {
    method: 'POST',
    headers: {
      Authorization: `Basic ${base64credentials}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    },
    body: 'grant_type=client_credentials',
  });
  const json = await res.json();
  const newToken = json.access_token;
  console.log('token is', newToken);
  return newToken;
}
